// client/src/pages/Login/Login.jsx
import React, { useState } from 'react';
import axios from 'axios'; // Import Axios

// ... other imports and component definition ...

const Login = () => {
    // ... useState definitions for email, password, rememberMe ...
    const [error, setError] = useState(null); // State to display errors

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null); // Clear previous errors

        try {
            // 1. Define the API endpoint (assumes React is configured to proxy /api or you use the full path)
            const response = await axios.post('http://localhost:5000/api/auth/login', { 
                email, 
                password 
            });

            // 2. Handle successful response
            const token = response.data.token;
            console.log('Login Success! Token:', token);

            // 3. Store the token (usually in local storage or cookies)
            localStorage.setItem('token', token);

            // 4. Redirect the user (e.g., to the dashboard)
            // history.push('/dashboard'); 

        } catch (err) {
            // 5. Handle errors from the server (e.g., 401 Invalid Credentials)
            if (err.response && err.response.data && err.response.data.msg) {
                setError(err.response.data.msg);
            } else {
                setError('Login failed. Please try again.');
            }
            console.error(err);
        }
    };

    return (
        // ... JSX structure ...
        <form onSubmit={handleSubmit} className="login-form">
            {/* Display error message */}
            {error && <div style={{ color: 'red', marginBottom: '15px' }}>{error}</div>}

            {/* ... email, password inputs, etc. ... */}
        </form>
    );
};

export default Login;
