// RENTHIVE/client/src/pages/ForgotPassword/ForgotPassword.jsx

import React, { useState } from 'react';
import axios from 'axios';
import './ForgotPassword.css'; // Don't forget to create this CSS file!

const API_BASE_URL = 'http://localhost:5000/api/auth';

const ForgotPassword = ({ history }) => {
    const [identifier, setIdentifier] = useState(''); // Holds email or phone number
    const [message, setMessage] = useState('');
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError(null);

        // Basic validation for an identifier
        if (!identifier.trim()) {
            return setError("Please enter your email or phone number.");
        }

        try {
            // API call to the backend endpoint
            const response = await axios.post(`${API_BASE_URL}/forgot-password`, { 
                identifier // Will be the email or phone number
            });

            // The backend returns a generic success message for security
            setMessage(response.data.msg || "Instructions sent. Check your inbox.");
            setIdentifier(''); // Clear the input field

            // In a real app, you would then navigate the user to the OTP entry screen.
            // setTimeout(() => history.push('/verify-otp'), 2000); 

        } catch (err) {
            // Display any specific error from the server (e.g., rate limiting)
            setError('Could not process the request. Please try again later.');
            console.error(err);
        }
    };

    return (
        <div className="forgot-container">
            <div className="forgot-card">
                <a href="/login" className="back-arrow">
                    <span role="img" aria-label="Back">⬅️</span>
                </a>
                <h2 className="forgot-title">Find your account</h2>
                <p className="forgot-subtitle">
                    Enter your email or phone number. <a href="#">Can't reset your password?</a>
                </p>

                {/* Display messages */}
                {message && <div className="success-message">{message}</div>}
                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit} className="forgot-form">
                    <input 
                        type="text" 
                        placeholder="Email or phone number (+977...)"
                        value={identifier}
                        onChange={(e) => setIdentifier(e.target.value)}
                        className="input-field"
                        required
                    />

                    {/* Button to continue based on input */}
                    <button type="submit" className="continue-button">Continue</button>
                </form>

                {/* Secondary Option: Find by mobile number (handled by same form input logic) */}
                <button 
                    className="mobile-find-button"
                    onClick={() => document.getElementById('identifier-input').focus()} // Focuses the main input
                >
                    Find by mobile number
                </button>
            </div>
        </div>
    );
};

export default ForgotPassword;
