// RENTHIVE/client/src/pages/Login/Login.jsx

import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';
import RenthiveLogo from '../../assets/Logo.png'; 
import LoginIllustration from '../../assets/Login_page.png'; 
// REMOVED: FaFacebookF, FaGoogle, FaApple imports

const API_BASE_URL = 'http://localhost:5000/api/auth';

const Login = ({ history }) => { // Assuming you use history for navigation
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState(null); 
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword(prevShowPassword => !prevShowPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    // ... (Login API call logic remains the same) ...
  };

  // NEW FUNCTION: Handles click on Forgot Password link
  const handleForgotPassword = async (e) => {
      e.preventDefault();
      // For this example, we'll navigate to a separate route for the reset process.
      // You would use react-router-dom's history.push here.
      console.log("Navigating to Forgot Password flow...");
      // history.push('/forgot-password'); // Uncomment if using react-router-dom
  };


  return (
    <div className="login-container">
      <div className="login-form-card">
        <img src={RenthiveLogo} alt="RentHive Logo" className="logo" />
        <h1 className="title">Login</h1>
        <p className="subtitle">Login to access your travelwise account</p>

        {error && <div style={{ color: 'red', marginBottom: '15px', fontSize: '14px' }}>{error}</div>}

        <form onSubmit={handleSubmit} className="login-form">
          {/* Email Input */}
          <label htmlFor="email">Email</label>
          <input 
            type="email" 
            id="email" 
            placeholder="john.doe@gmail.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          {/* Password Input */}
          <label htmlFor="password">Password</label>
          <div className="password-input-wrapper">
            <input 
              type={showPassword ? 'text' : 'password'}
              id="password" 
              placeholder="............"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <span 
              className="password-toggle-icon"
              onClick={togglePasswordVisibility}
            >
              {showPassword ? 'üôà' : 'üëÅÔ∏è'}
            </span> 
          </div>

          <div className="remember-forgot-row">
            <label className="checkbox-container">
              <input 
                type="checkbox" 
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
              />
              Remember me
            </label>
            {/* üëà UPDATED: Attached handler to prevent default browser navigation */}
            <a href="/forgot-password" onClick={handleForgotPassword} className="forgot-password">Forgot Password</a>
          </div>

          <button type="submit" className="login-button">Login</button>
        </form>

        <p className="signup-link">
          Don't have an account? <a href="/register">Sign up</a>
        </p>

        {/* REMOVED: Social Login Separator */}
        {/* REMOVED: Social Buttons */}
      </div>

      <div className="login-illustration-side">
        <img src={LoginIllustration} alt="House and Keys" />
      </div>
    </div>
  );
};

export default Login;
